const getTotalDay2 = function(stringValues) {

    var elfBag = { red: 62, green: 13, blue: 14 };
    stringValues = "Game 1: 9 red, 2 green, 13 blue; 10 blue, 2 green, 13 red; 8 blue, 3 red, 6 green; 5 green, 2 red, 1 blue";
    stringValues = stringValues.split(/:|;/);
    //console.log(stringValues);
    var gameDeck = [];
    for (var element in stringValues) {
        gameDeck.push(stringValues[element]);
    }
    var game = gameDeck.shift();
    //console.log("Game: "+game);
    var bag = { red: 0, green: 0, blue: 0 };

    for (var hands in gameDeck) {
        var deck = [];
        deck = gameDeck[hands].split(",");

        for (var cubes in deck) {
            deck[cubes] = deck[cubes].trimStart();
            var [num, color] = deck[cubes].split(" ");
            bag[color] = Number(bag[color])+Number(num);
        }
    }

    var possible = true;

    for (const [key, value] of Object.entries(bag)) {
        if (elfBag[key] > value)
            possible = false;
    }
    if (!possible)
        console.log(game);
    else   
        console.log(possible);

    //console.log(bag);
    //12 red, 13 green, 14 blue
    //return stringValues;
}

export default getTotalDay2;
