const getTotalDay2 = function(stringValues) {

    var elfBag = { red: 12, green: 13, blue: 14 };
    stringValues = "Game 5: 6 red, 1 blue, 3 green; 2 blue, 1 red, 2 green";
    let total = 0;

    //console.log(stringValues.length);

    for (let i = 0; i < stringValues.length; i++) {
        var thisValue = stringValues.split(/:|;/); 
        //var thisValue = stringValues[i].split(/:|;/); 
        //console.log(stringValues);
        var gameDeck = [];
        for (var element in thisValue) {
            gameDeck.push(thisValue[element]);
        }
        var game = gameDeck.shift();
        //console.log("Game: "+game);
        var bag = { red: 0, green: 0, blue: 0 };

        for (var hands in gameDeck) {
            var deck = [];
            deck = gameDeck[hands].split(",");

            for (var cubes in deck) {
                deck[cubes] = deck[cubes].trimStart();
                var [num, color] = deck[cubes].split(" ");
                bag[color] = Number(bag[color])+Number(num);
            }
        }

        var possible = false;

        for (const [key, value] of Object.entries(bag)) {
            //console.log(`Key: ${key}, Value: ${value}`);
            //console.log(`${elfBag[key]}--${value}`);
            if (elfBag[key] >= value)
                possible = true;
        }
        console.log(possible);
        if (possible === true) {
            console.log(game);
            var [game, nbr] = game.split(" ")
            total = total + Number(nbr);
        }
    }
    return total;
}

export default getTotalDay2;
