import logo from './pluginIcon.png';
import './App.css';
import React from 'react';
import { useState, useEffect } from 'react';

function getTotal() {
  var total = 0;
  var totalString = 0;
  var count = 0;
  var tempValue = ["shrzvdcghblt21", "sixdddkcqjdnzzrgfourxjtwosevenhg9", "threevt1onegxgvc9flk"];
  //var tempValue = "threevt1onegxgvc9flk";

  for (let i = 0; i < txtArray.length; i++) {
    for (const char of tempValue) {
      //console.log(char);
      if (!isNaN(char)) {
        //alert(char);
        count = count + 1;
        totalString = totalString + Number(char);
      }
    }
    if (count == 1) {
      totalString = totalString*2;
    }
    total = total + totalString;
    count = 0;
    totalString = 0;
  }
  //alert ("count: "+count);
  return "totalString: "+totalString;
  //alert ("totalString: "+totalString);


  /*
  for (let i = 0; i < txtArray.length; i++) {
    for (const char of txtArray[i]) {
      //console.log(char);
      if (!isNaN(char)) {
        //alert(char);
        count = count + 1;
        totalString = totalString + char;
      }
      alert (totalString);
      alert (count);
      count = 0;
    }
    totalString = 0;
    total = 0;
  }
  */

}

function App() {

  //Declare useState
  const [txtData, setTxtData] = useState("En cours de chargement...");

  // Declare useEffect that fetch the data in the text file
  useEffect(() => {
    fetch(process.env.PUBLIC_URL + "/input.txt")
    .then(r => r.text())
    .then(text => {
      setTxtData(text)
    });
  }, [])

  var txtArray = txtData.split("\n");

   return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          <code>adventofcode.com/2023</code> 
        </p>
        <a
          className="App-link"
          href="https://adventofcode.com/2023/day/1"
          target="_blank"
          rel="noopener noreferrer"
        >
          Day 1: Trebuchet?!
        </a>
      </header>
      <div className="App-body">
        <div className="display-linebreak">
           {getTotal(txtArray)} 
           {/*txtArray.join('\r\n')*/} 
        </div>
      </div>
    </div>
  );
}

export default App;
