function getPossibility (deck) { 
    var elfBag = { red: 12, green: 13, blue: 14 };
    var possible = true;
    console.log(deck);
    for (var cubes in deck) {
        deck[cubes] = deck[cubes].trimStart();
        var [num, color] = deck[cubes].split(" ");
        //bag[color] = Number(bag[color])+Number(num);
        //console.log(`Color: ${color}, #: ${num}`);
        if (elfBag[color] >= num) {
            possible = true;
        }
        else {
            return false;
        }
    }
    return possible; 
}

